    public historiadorEdit(Empregado empregado, Modelo modelo) {
        this.empregado = empregado;
        this.modelo = modelo;
        JFrame frame = new JFrame("Inserir Hangar");
        $$$setupUI$$$();

        frame.setContentPane(panel1);
        frame.setDefaultCloseOperation(JFrame.EXIT_ON_CLOSE);
        frame.pack();
        frame.setVisible(true);

        voltarSairButton.addActionListener(e -> {
            new HistoriadorI(empregado);
            frame.dispose();
        });
        inserirButton.addActionListener(new ActionListener() {
            @Override
            public void actionPerformed(ActionEvent e) {
                try {
                    Modelo.Tipo tipo = (tipoComboBox.getSelectedIndex() == 0) ? Modelo.Tipo.AVIAO : Modelo.Tipo.REPLICA;


                    Modelo modeloInterno = new Modelo(marcaTextField.getText(), LocalDate.parse(dataDeProducao.getText(), DateTimeFormatter.ofPattern("dd/MM/yyyy")), Double.parseDouble(comprimentotextField.getText()),
                            Double.parseDouble(envergaduraTextField.getText()), historicoTextPane.getText(), tipo, areaDeAtuacaoField.getText(),
                            materialUsadoField.getText(), ((Hangar) hangarComboBox.getSelectedItem()), estadoField.getText());
                    modeloInterno.setCodigo(modelo.getCodigo());
                    if (ModeloDAO.getInstance().editar(modeloInterno)) {
                        JOptionPane.showMessageDialog(frame, "Modelo editado com sucesso!");
                        new HistoriadorI(empregado);
                        frame.dispose();
                    } else
                        JOptionPane.showMessageDialog(frame, "Edição de modelo mal-sucedida.");
                } catch (DateTimeParseException exception) {
                    JOptionPane.showMessageDialog(frame, "A data está errada de alguma maneira.");
                } catch (NumberFormatException exception) {
                    JOptionPane.showMessageDialog(frame, "Campos numericos precisam ter numeros.");
                } catch (Exception exception) {
                    JOptionPane.showMessageDialog(frame, exception.getMessage());
                }
            }
        });
    }

    private void createUIComponents() {
        nomeIndividuoLabel = new JLabel(empregado.getNomeSobrenome());

        int indice = 0;

        for (Hangar hangar : HangarDAO.getInstance().pesquisarTudo()) {
            if (hangar.equals(modelo.getHangar())) break;
            indice++;
        }


        hangarComboBox = new JComboBox<>(HangarDAO.getInstance().pesquisarTudo().toArray());
        hangarComboBox.setSelectedIndex(indice);

        tipoComboBox = new JComboBox<>(new String[]{"Aviao", "Modelo"});
        tipoComboBox.setSelectedIndex(((modelo.getTipo() == Modelo.Tipo.AVIAO) ? 0 : 1));


        tipoComboBox = new JComboBox<>(new String[]{"Aviao", "Modelo"});


        codigoTextField = new JLabel(String.valueOf(modelo.getCodigo()));
        marcaTextField = new JTextField(modelo.getMarca());
        try {
            dataDeProducao = new JFormattedTextField(new MaskFormatter("##/##/####"));
            dataDeProducao.setText(modelo.getDataProducao().format(DateTimeFormatter.ofPattern("dd/MM/yyyy")));
        } catch (Exception e) {
            e.printStackTrace();
        }
        comprimentotextField = new JTextField(String.valueOf(modelo.getComprimentoMetros()));
        envergaduraTextField = new JTextField(String.valueOf(modelo.getEnvergaduraMetros()));
        historicoTextPane = new JTextPane();
        historicoTextPane.setText(modelo.getHistoria());
        areaDeAtuacaoField = new JTextField(modelo.getAreaAtuacao());
        materialUsadoField = new JTextField(modelo.getMaterialUsado());
        estadoField = new JTextField(modelo.getEstado());


    }

